angular.module('searaClientApp')
  .controller('FoldersIndexCtrl',function (GlobalService, AuthService, $scope, $routeParams, $location, $http) {

		$scope.data = [];
    $scope.path = $routeParams.path;
    $scope.editable = false;
    $scope.inFavorites = false;
		$scope.show = false;
		$scope.imageSource = '';

		var token = AuthService.currentUser.authentication_token;
    var apiUrl = GlobalService.apiUrl;

    $scope.testClick = function(){
			alert("yes");
		}
		
		$scope.browse = function(path){
			$http.get(apiUrl+'folders/?path='+path+'&auth_token='+token)
				.success(function (data, status){
					$scope.data = data;
					$scope.status = status;
					switch($scope.data.current.permission){
						case 'cloud':
							if(AuthService.currentUser.group_id<2){
								$scope.editable = true;
							}
							break;
						case 'user':
							if(AuthService.currentUser.id == $scope.data.current.creator_id){
								$scope.editable = true;
								$scope.inFavorites = true;
							}
						}
					})
				.error(function(data, status){
					console.log(data.message);
			});
		};

		$scope.showImage = function (asset) {
			$scope.imageSource = apiUrl+'assets/'+asset.id+'?auth_token='+token;	
			$scope.show = true;
		}

		$scope.showThumbnail = function (asset) {
			return apiUrl+'assets/'+asset.id+'/thumbnail?auth_token='+token;	
		}

		$scope.hideImage = function (){
			$scope.show = false;
			$scope.imageSource = '';
		}

});
	
	





